import React, { Component } from 'react'

import './Navbar.css'
import { Button } from '../Button'
import ap from './ap.png'
import dn from './dn.png'
import jw from './jw.png'
import md from './md.png'

class SpeakerPartners extends Component{  constructor(props) {
    super(props);
    this.switchImage = this.switchImage.bind(this);
    this.state = {
      currentImage: 0,
      images: [
        ap,
        dn,
        jw,
        md
      ]
    };
  }

  switchImage() {
    if (this.state.currentImage < this.state.images.length - 1) {
      this.setState({
        currentImage: this.state.currentImage + 1
      });
    } else {
      this.setState({
        currentImage: 0
      });
    }
    return this.currentImage;
  }

  componentDidMount() {
    setInterval(this.switchImage, 1000);
  }
    render(){
        return(   
        <div className="slideshow-container">
            <img
            src={this.state.images[this.state.currentImage]}
            alt="cleaning images"
            />
        </div>
        );
    }
}

export default SpeakerPartners
